TFA.CSGO = TFA.CSGO or {}
TFA.CSGO.SkinLangStrings = TFA.CSGO.SkinLangStrings or {} --Autogenerated in csgo_lang.lua

if not TFA.CSGO.ItemData then
	TFA.CSGO.ItemData = TFA.ParseKeyValues( "tfa/txt/items_game.lua", "LUA", true, false, true )
end
if not TFA.CSGO.LanguageData then
	TFA.CSGO.LanguageData = TFA.ParseKeyValues( "tfa/txt/csgo_english.lua", "LUA", true, false, true )
end

if TFA.CSGO.ItemData and TFA.CSGO.LanguageData then
	if not TFA.CSGO.ItemData then return end
	for k,v in pairs( TFA.CSGO.ItemData.items_game.paint_kits ) do
		if v.description_tag then
			TFA.CSGO.SkinLangStrings[v.name] = string.lower( v.description_tag )
		end
	end
	if language and language.Add then
		local str
		for k,v in pairs( TFA.CSGO.SkinLangStrings ) do
			str = v
			if string.StartWith(str, "#") then
				str = string.sub( str, 2)
			end
			if TFA.CSGO.LanguageData.lang.tokens[str] then
				language.Add( str, TFA.CSGO.LanguageData.lang.tokens[str] )
			end
		end
	end
end