--Skin System
if SERVER then
	AddCSLuaFile()
	util.AddNetworkString("TFA_CSGO_SKIN")
	util.AddNetworkString("TFA_CSGO_SKIN_BROADCAST")

	net.Receive("TFA_CSGO_SKIN", function(length, client)
		local ent = net.ReadEntity()

		if IsValid(ent) and ent.Owner == client then
			local skin = net.ReadString()
			local skinchecker = string.lower(skin and skin or "")

			if skinchecker == "" or skinchecker == "stock" then
				net.Start("TFA_CSGO_SKIN_BROADCAST")
				net.WriteEntity(ent)
				net.WriteString("")
				net.SendOmit(client)
				ent:SetMaterial("")
			end

			ent:SetNWString("skin", skin and skin or "")
			--[[
			ent:SetNWString("skin",skin and skin or "")
			if ent.UpdateSkin then
				if ent.CallOnClient then
					ent:CallOnClient("UpdateSkin","")
				end
				ent:UpdateSkin()
			end
			]]
			--
			net.Start("TFA_CSGO_SKIN_BROADCAST")
			net.WriteEntity(ent)
			net.WriteString(skin)
			net.SendOmit(client)
		end
	end)
end

if CLIENT then
	net.Receive("TFA_CSGO_SKIN_BROADCAST", function()
		local ent = net.ReadEntity()

		if IsValid(ent) then
			local skin = net.ReadString()
			ent.Skin = skin and skin or ""
			ent:SetNWString("skin", skin and skin or "")

			if ent.UpdateSkin then
				ent:UpdateSkin()
			end
		end
	end)
end

