local malecount = #rp.names.Male
local femalecount = #rp.names.Female
local lastcount = #rp.names.Last

function rp.names.Random(sex)
  if sex == 1 then
	 return (rp.names.Male[math.random(1, malecount)] .. ' ' .. rp.names.Last[math.random(1, lastcount)])
  elseif sex == 2 then
    return (rp.names.Female[math.random(1, femalecount)] .. ' ' .. rp.names.Last[math.random(1, lastcount)]..'а')
  end
end

function rp.names.RandomName(sex)
  if sex == 1 then
   return (rp.names.Male[math.random(1, malecount)])
  elseif sex == 2 then
    return (rp.names.Female[math.random(1, femalecount)])
  end
end

function rp.names.RandomLast(sex)
  if sex == 1 then
   return (rp.names.Last[math.random(1, lastcount)])
  elseif sex == 2 then
    return (rp.names.Last[math.random(1, lastcount)])..'а'
  end
end