local tFontData = {
	font = "Font Awesome 5 Pro Solid",
	extended = true,
	weight = 900,
}

local tFontSizes = { 8, 12, 16, 24, 32, 48, 64 }

local sSupportedSizes = "[" -- We will list all the supported font sizes for errors here
for _, nSize in pairs( tFontSizes ) do
	tFontData.size = nSize
	surface.CreateFont("FontAwesome" .. tostring(nSize) .. "pt", tFontData)
	sSupportedSizes = sSupportedSizes .. tostring(nSize) .. ","
end
-- Remove the last , and add the ending ] to the list of supported font sizes
sSupportedSizes = string.sub( sSupportedSizes, 0, -1 ) .. "]"

-- Use the font awesome docs to find icon names easier. https://fontawesome.com/icons?d=gallery&s=solid&m=free
-- Make sure you filter by "Free" and "Solid" Icons only as those are the only ones added.
function FontAwesome.CreateIcon( aIconNameOrNumber, nSize )
	local fontName = FontAwesome.GetFont( nSize )

	local vguiIcon = vgui.Create("fontAwesomeIcon")
	vguiIcon:SetFont(fontName)
	vguiIcon:SetIcon( aIconNameOrNumber )

	return vguiIcon
end

-- Returns the font name matching the size. NOTE: Make sure the size is one of the sizes in tFontSizes as those are the only supported ones
function FontAwesome.GetFont( nSize )
	if not table.HasValue( tFontSizes, nSize ) then error("Invalid FontAwesome icon size. Supported sizes: " .. sSupportedSizes) end
	return "FontAwesome" .. tostring(nSize) .. "pt"
end

function FontAwesome.GetIcon( aIconNameOrNumber )
	if type(aIconNameOrNumber) == "string" then
		local nIconNumber = FontAwesome.Icons[ aIconNameOrNumber ]
		sIconChar = nIconNumber and utf8.char(nIconNumber) or "X"
	else
		sIconChar = utf8.char( aIconNameOrNumber )
	end
	return sIconChar
end


local FONTAWESOME_ICON = {}


function FONTAWESOME_ICON:SetIcon( aIconNameOrNumber )
    self.Icon = FontAwesome.GetIcon( aIconNameOrNumber )
end

function FONTAWESOME_ICON:SetColor( cColor )
    self.Color = cColor
end

function FONTAWESOME_ICON:GetColor()
    return self.Color
end

function FONTAWESOME_ICON:SetIconSize( nSize )
    self.IconSize = nSize
end

FONTAWESOME_ICON.SetText = FONTAWESOME_ICON.SetIcon

function FONTAWESOME_ICON:Paint( w, h )
    if not self.Icon then return end

    draw.SimpleText( self.Icon, FontAwesome.GetFont( self.IconSize or 32 ), w / 2, h / 2, self:GetColor(), TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER )
end


vgui.Register( "FontAwesomeIcon", FONTAWESOME_ICON, "DPanel" )

concommand.Add( "FontAwesomeTest", function()
	local frame = vgui.Create( "DFrame" )
	frame:Center()
	frame:StretchToParent( 0, 0, 0, 0 )
	frame:MakePopup()

	local scroll = frame:Add( "SRP_ScrollPanel" )
	scroll:Dock( FILL )

	local layout = scroll:Add( "DIconLayout" )
	layout:Dock( FILL )

	local text = scroll:Add( "DLabel" )
	text:SetFont( "FontAwesome24pt" )
	text:Dock( FILL )
	text:SetWrap( true )
	text:SizeToContents()

	local val = ""
	for k, i in pairs( FontAwesome.Icons ) do 
		val = val .. utf8.char( i )
	end

	text:SetText( val )
end )
